func init() {
	_abi, err := abi.JSON(strings.NewReader(punkABI))
	if err != nil {
		panic("Failed to parse punk abi")
	}
	PunkABI = _abi

	_abi, err = abi.JSON(strings.NewReader(punkDataABI))
	if err != nil {
		panic("Failed to parse punk data abi")
	}
	PunkDataABI = _abi
}

// PunkAssignLog
// event Assign(address indexed to, uint256 punkIndex)
type PunkAssignLog struct {
	To        common.Address // indexed
	PunkIndex *big.Int
}

// PunkTransferLog
// event PunkTransfer(address indexed from, address indexed to, uint256 punkIndex)
type PunkTransferLog struct {
	From      common.Address // indexed
	To        common.Address // indexed
	PunkIndex *big.Int
}

// PunkBoughtLog
// event PunkBought(uint indexed punkIndex, uint value, address indexed fromAddress, address indexed toAddress)
type PunkBoughtLog struct {
	PunkIndex *big.Int // indexed
	Value     *big.Int
	From      common.Address // indexed
	To        common.Address // indexed
}

func ToPunkAssignLog(log *types.Log) (*PunkAssignLog, error) {
	var assign PunkAssignLog
	if err := PunkABI.UnpackIntoInterface(&assign, "Assign", log.Data); err != nil {
		return nil, err
	}
	assign.To = common.BytesToAddress(log.Topics[1].Bytes())
	return &assign, nil
}

func ToPunkTransferLog(log *types.Log) (*PunkTransferLog, error) {
	var transfer PunkTransferLog
	if err := PunkABI.UnpackIntoInterface(&transfer, "PunkTransfer", log.Data); err != nil {
		return nil, err
	}
	transfer.From = common.BytesToAddress(log.Topics[1].Bytes())
	transfer.To = common.BytesToAddress(log.Topics[2].Bytes())
	return &transfer, nil
}

func ToPunkBoughtLog(log *types.Log) (*PunkBoughtLog, error) {
	var bought PunkBoughtLog
	if err := PunkABI.UnpackIntoInterface(&bought, "PunkBought", log.Data); err != nil {
		return nil, err
	}
	index := new(big.Int).SetBytes(log.Topics[1].Bytes())
	bought.PunkIndex = index
	bought.From = common.BytesToAddress(log.Topics[2].Bytes())
	bought.To = common.BytesToAddress(log.Topics[3].Bytes())
	return &bought, nil
}
